import React, { useState } from "react";
import "./ExpenseList.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";



function ExpenseList(props) {
  const [chosenYear, setChosenYear] = useState("");

  const chosenYearHandler = (selectedYear) => {
    setChosenYear(selectedYear);
  };

  const [expenses, setExpenses] = useState(dummy_expenses);

  // Important to do this as an Array, cause this function can only take one argument
  // setExpenses((prevExpenses) => {
  //   return [props.newExpenseItem, ...prevExpenses];
  // });

  return (
    <Card className="expenses">
      <ExpensesFilter selected={chosenYear} onChosenYear={chosenYearHandler} />
      {props.expenses.map((expense) => (
        <ExpenseItem
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
}

export default ExpenseList;
