import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  // const [enteredTitle, setEnteredTitle] = useState('');
  // const [enteredAmount, setEnteredAmount] = useState('');
  // const [enteredDate, setEnteredDate] = useState('');
  // In this case it is better to group all states:

  const [userInput, setUserInput] = useState({
    enteredTitle: '',
    enteredAmount: '',
    enteredDate: ''
  });

  const titleChangeHandler = (event) => {
    // setEnteredTitle(event.target.value);

    // OK-ish way to use one state:
    // setUserInput({
    //   ...userInput,
    //   enteredTitle: event.target.value,
    // });

    // Better way to use one state with anonymous arrow function
    console.log(event.target.value);
  };

  const amountChangeHandler = (event) => {
    // setEnteredAmount(event.target.value);
    setUserInput ({
      ...userInput,
      enteredAmount: event.target.value
    })
    console.log(event.target.value);
  };

  const dateChangeHandler =(event) => {
    // setEnteredDate(event.target.value);
    setUserInput ({
      ...userInput,
      enteredDate: event.target.value
    })
    console.log(event.target.value);
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;

// this "event.target.value" thing is cool and super useful:
// Event is basically an object with a lot of properties that can be used
// State is used to store value in variables that are detached from component's life cycle
// so when the components are reloaded, and they are constantly being reloaded, value remains somewhere
// In useState we always use a string in this example, cause the targe.value is always a string
// Then obviously you end up with multiple states per component, it's normal hee
// Individual state slices are more popular
