import React, { useState } from "react";
import "./ExpenseList.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const dummy_expenses = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

function ExpenseList(props) {
  const [chosenYear, setChosenYear] = useState("");

  const chosenYearHandler = (selectedYear) => {
    setChosenYear(selectedYear);
  };

  const [expenses, setExpenses] = useState(dummy_expenses);

  // Important to do this as an Array, cause this function can only take one argument
  setExpenses([props.newExpenseItem , ...expenses]);

  return (
    <Card className="expenses">
      <ExpensesFilter selected={chosenYear} onChosenYear={chosenYearHandler} />
      {dummy_expenses.map((expense) => (
        <ExpenseItem
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
}

export default ExpenseList;
