import React, { useState } from "react";
import "./ExpenseItem.css";
import ExpenseDate from "./ExpenseDate";
import Card from "../UI/Card";

// Using double underscores in the classnames
// Components only receive ONE parameter, which is an object, that we need to dissect into properties
// Component can be self-closing, if there is nothing in between
// In "onClick = {smth}" there is no need to add "()" to the function
// If you add "()", the function will be executed when document is parsed
// Convention is to end names of event functions with "Handler"

// Hooks can only be called within the component function - not outside or in nested functions
// useState returns an array with 2 elements - current state value, and function to update that value
// Then you just pass new value as an argument inside the setTitle function


function ExpenseItem(props) {
  const [title, setTitle] = useState(props.title);

  const smthHandler = () => {
    setTitle('Updated!');
  };

  return (
    <Card className="expense-item">
      <ExpenseDate date={props.date} />
      <div className="expense-item__description">
        <h2>{title}</h2>
        <div className="expense-item__price">${props.amount}</div>
      </div>
      <button onClick={smthHandler}>Knopochka</button>
    </Card>
  );
}

export default ExpenseItem;


// Listen to lecture #49 again to better understand how State works
// Need to remember to check out css of components, cause it looks cool
// Instead of onClick you can use onInput to listen to keystrokes, or onChange that triggers on any change in the element, it's pretty cool
