import React, { useState } from "react";
import "./ExpenseList.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

function ExpenseList(props) {
  const [chosenYear, setChosenYear] = useState("");

  const chosenYearHandler = (selectedYear) => {
    setChosenYear(selectedYear);
  };

  const filteredExpenses = props.expenses.filter((item) => {
    return item.date.getFullYear().toString() === chosenYear;
  });

  return (
    <Card className="expenses">
      <ExpensesFilter selected={chosenYear} onChosenYear={chosenYearHandler} />
      {filteredExpenses.length === 0 && <p>No expenses registered</p>}
      {filteredExpenses.length > 0 &&
        filteredExpenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
    </Card>
  );
}

export default ExpenseList;

// We are using the trick with conditional operators here
// x === 3 ? if true, something happens : otherwise, something else happens
