import React from 'react';
import './Card.css';

// This is a re-usable wrapper component
// I guess it is conventionally universal or something
// There is a bit of a trick here with classes being passed s list of classes as a string
// This is similar to what I was doing with passing "px" values in BlackJack

function Card(props) {
    const classes = 'card ' + props.className;

  return <div className={classes}>{props.children}</div>;
}

export default Card;
